import React, { useContext } from 'react'
import { useLoaderData } from 'react-router-dom'
import { iCategory, iProduct} from '../../types'
import GridProduct from '../Product/GridProduct'

import styles from './LandingPage.module.css'
import { SearchContext } from '../../'
// import Product from './Product/Product'


const LandingPage = () => {
    const {search} = useContext(SearchContext)

    console.log("Landing page search", search)
    const categories: iCategory[] = useLoaderData() as iCategory[];

    const products:iProduct[][] = categories.map( (category) => {
        const {id, createdAt, name, products} = category
        const newProducts = products.map( prod => ({...prod,categoryName:name})).filter(e => e.name.includes(search) || e.details.includes(search))
        return (newProducts)
    })

    const test = products.flatMap(e => e) as iProduct[]
    console.log(test,'test')



return(<div>
<div className={styles.container}>
   {test.map( product => <GridProduct product={product}/> )}

</div>

</div>)


}


export default LandingPage
