import React, {useState} from 'react'
import {ModalWrapper, StarRating} from '../'
import styles from './ReviewForm.module.css'
import {iProduct} from '../../types'
interface iReviewFormProps {
    showReviewForm: Boolean;
    setShowReviewForm: React.Dispatch<React.SetStateAction<Boolean>>;
    product: iProduct
  }

const ReviewForm:React.FC<iReviewFormProps> = ({setShowReviewForm}) => {
    const [rating, setRating] = useState(0);
    const [formData, setFormData] = useState({title:'',content:'', name:'', email: ''})
    console.log("ReviewForm ReviewForm")

    const handleRatingChange = (newValue: number) => {
        setRating(newValue);
      };

      const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { id, value } = e.target;

        setFormData(prevState => ({ ...prevState, [id]: value }));
      };




      const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        // Here you can submit the form data, such as rating, title, and description
        console.log('Rating:', rating);
        console.log('Title:', formData.title);
        console.log('Description:', formData.content);
        // Reset the form fields if needed
        setShowReviewForm(false)
      };

    return(
    <ModalWrapper>
  <form onSubmit={handleSubmit} className={styles.myForm}>
      <h1>
        Rating <StarRating value={rating} onChange={handleRatingChange} />
      </h1>
      <input
        id="title"
        type="text"
        value={formData.title}
        onChange={handleChange}
        placeholder="Title"
        className={styles.formInput}
      />

        <input
        id="content"
        type="text"
        value={formData.content}
        onChange={handleChange}
        placeholder="Content"
        className={styles.formInput}
      />

        <input
        id="name"
        type="text"
        value={formData.name}
        onChange={handleChange}
        placeholder="Name"
        className={styles.formInput}
      />
      <input
        id="email"
        type="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="Email"
        className={styles.formInput}
      />
      <button type="submit" className={styles.formButton}>
        Submit
      </button>
      <button type="submit" className={styles.formCancel}
      onClick={() => setShowReviewForm(false)}
      >
        Cancel
      </button>
    </form>
    </ModalWrapper>
)
}


export default ReviewForm
