import React from 'react';

interface StarProps {
  filled: boolean;
}

const Star: React.FC<StarProps> = ({ filled }) => {
  return (
    <span style={{ color: filled ? 'yellow' : 'grey' }}>
      &#9733;
    </span>
  );
};

interface RatingProps {
  value: number; // Rating value out of 5
}

const StarRating: React.FC<RatingProps> = ({ value }) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(<Star key={i} filled={i <= value} />);
  }
  return <div>{stars}</div>;
};

export default StarRating;
