import React, {useState} from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import { LandingPage, NewReview, ViewProduct} from './components'
import reportWebVitals from './reportWebVitals';
import { loadCategories } from './utils'
import { RouterProvider, createBrowserRouter } from 'react-router-dom'
import RootLayout from './routes/RootLayout'


const sampleProd =  {
  "createdAt": "2023-01-08T12:30:17.338Z",
  "name": "Deaeun's Shoe",
  "image": "data:image/jpeg;base64,",
  "price": 4564,
  "currency": "$",
  "details": "Stephen Curry Shoes",
  "id": "65",
  "categoryId": "2",
  "dateCreated":"createdAt",
  "category":"2"
}
//landing page
//add review modal
  // user clicks submit button
    //On success, should redirect back to the Product Review page with the newly added review on the top of the Review List On error, show the error message
//edit review modal
const router = createBrowserRouter([
  {path:'/', element: <RootLayout />,
children: [
  {path: '/', element: <LandingPage/>, loader: loadCategories},
  {path: '/testSampleProd', element: <ViewProduct product={sampleProd}/>, loader: loadCategories},

  // {path: '/edit-review', element: <NewReview/>}

]}
])

const root = ReactDOM.createRoot(
  document.getElementById('root') as HTMLElement
);
root.render(
  <React.StrictMode>
    <RouterProvider router={router}/>
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
