import React, {useContext} from 'react'
import {iCategory} from '../../types'
import {FilterContext} from '../../'
import styles from './FilterRadio.module.css'

interface iFilterRadioProps{
    categories:iCategory[]
}
const FilterRadio:React.FC<iFilterRadioProps> = ({categories = []})=> {
    const {filter, setFilter} = useContext(FilterContext)
    const options:string[] = categories.map((e:iCategory) => e.name)


    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const val = e.target.value;
        setFilter((currState: string[]) => {
          if (currState.includes(val)) {
            // If the value is already in the state, remove it
            return currState.filter(item => item !== val);
          } else {
            // Otherwise, add it to the state
            return [...currState, val];
          }
        })};

return(<>
    {options.map( option => 
    <div>
      <input
          className={styles.input}
          type="checkbox"
          checked={filter.includes(option)}
        //   onChange={handleChange}
      />

    <label className={styles.label}>
        {option}
    </label>
    </div>


      )

      }
</>)

    }



export default FilterRadio
