import React, { useContext } from 'react'
import { useLoaderData } from 'react-router-dom'
import { iCategory, iProduct} from '../../types'
import GridProduct from '../Product/GridProduct'

import styles from './LandingPage.module.css'
import { SearchContext, FilterContext } from '../../'
// import Product from './Product/Product'


const LandingPage = () => {
    const {search} = useContext(SearchContext)

    const categories: iCategory[] = useLoaderData() as iCategory[];

    const products:iProduct[][] = categories.map( (category) => {
        const {id, createdAt, name, products} = category
        const newProducts = products.map( prod => ({...prod,categoryName:name}))
        .filter(e => e.name.toLowerCase().includes(search.toLowerCase()) || e.details.toLowerCase().includes(search.toLowerCase()))
        return (newProducts)
    })

    const filteredProducts = products.flatMap(e => e) as iProduct[]



return(<div>
<div className={styles.container}>
   {filteredProducts.map( product => <GridProduct product={product}/> )}

</div>

</div>)


}


export default LandingPage
